package firstweek;

public class program7 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub\
		int a=10;
		int i=0;
		for(i=0;i<=4;i++)
		{
			System.out.println(a);
			a-=2;
		}
		a=10;
		i=0;
		while(i<=4)
		{
			System.out.println(a);
			a-=2;
			i++;
			
		}

	}

}
